mapper.delete:
delete from conf_node_template
where node_id = #{nodeId}
and uid = #{uid}

mapper.update:
update conf_node_template
set  delete_flag = 1
where node_id = #{nodeId}
and uid = #{uid}

mapper.select:
select 

from conf_node_template
where node_id = #{nodeId}
and uid = #{uid} 
and delete_flag = #{deleteFlag}
