mapper.select:
    select 
    
    from conf_product_node
    
      
	        and product_id = #{productId}
	  
	  
	        and node_id = #{nodeId}
	  
	  
	        and uid = #{uid}

mapper.delete:
delete from conf_product_node
where node_id = #{nodeId}
and product_id = #{productId}

mapper.update:
 	update conf_product_node
 	set  delete_flag = 1
	where node_id = #{nodeId}
   and product_id = #{productId}

update conf_product_node
set effect = #{effect},
sequence = #{sequence}
where  node_id = #{nodeId}
  and product_id = #{productId}
  and uid = #{uid}
  and delete_flag = 0

	update conf_product_node
	set effect = #{effect},
	sequence = #{sequence}
	where  node_id = #{nodeId}
 	and product_id = #{productId}
 	and uid not in 
 	
          #{item}  
   
  and delete_flag = 0
