mapper.select:
  select t.* from conf_step_info t
  
	  
	        and t.node_id like '%${nodeId}%' 
	   
	   
	        and t.node_name like '%${nodeName}%'
	   
	   
	        and t.step_id = #{stepId}
	   
     
   order by t.node_id desc
   limit #{startNum},#{pageSize}

  select s.step_id, s.step_name from conf_step_info s, conf_product_step p
  
      s.step_id = p.step_id
	  
	        and p.product_id = #{productId} 
	   
	   
	        and s.node_id = #{nodeId}

	select count(1)
   from  conf_product_step ps 
   
   
  	 and ps.step_id = #{stepId}
  	
  	 
  		and ps.flow_id = #{flowId}
      
      
  		and ps.product_id = #{productId}
     
      
  		and ps.business_type = #{businessType}
     
  	and ps.delete_flag = 0

	select ps.id, ps.delete_flag
   from  conf_product_step ps 
   
   
  	 and ps.step_id = #{stepId}
  	
  	 
  		and ps.flow_id = #{flowId}
      
      
  		and ps.product_id = #{productId}
     
      
  		and ps.business_type = #{businessType}

	select ps.id, ps.step_id, ps.flow_id
   from  conf_product_step ps 
   
      
  		and ps.product_id = #{productId}
     
      
  		and ps.business_type = #{businessType}

	select ps.product_id
   from  conf_product_step ps 
   
      
  		and ps.step_id = #{stepId}
     
      
  		and ps.flow_id = #{flowId}
